# A = [A0, A1, A2, A3, A4, A5, A6, A7]

def combiner(A):
    N = 8
    idx = [0] * N
    Combined = []

    while any(idx[q] < len(A[q]) for q in range(N)):
        # 1) flush all ready single-qubit rotations
        made_progress = False
        for q in range(N):
            if idx[q] < len(A[q]) and A[q][idx[q]][0] in ("RX", "RY"):
                Combined.append(A[q][idx[q]])
                idx[q] += 1
                made_progress = True
        if made_progress:
            continue

        # 2) look for a matching MS at both heads
        # print('Step2')
        for q in range(N):
            if idx[q] < len(A[q]) and A[q][idx[q]][0] == "MS":
                head = A[q][idx[q]]
                _, _, wires = head
                other = wires[0] if wires[1] == q else wires[1]

                for j in range(idx[other], len(A[other])):
                    if A[other][j] == head:
                        Combined.append(head)
                        idx[q] += 1
                        idx[other] = j + 1
                        made_progress = True
                        break
                if made_progress:
                    break

        if made_progress:
            continue

        # 3) still nothing => log + abort
        print("Deadlock! queues non-empty but no ready ops.")
        print(" pointers:", idx)
        for q in range(N):
            if idx[q] < len(A[q]):
                print(f"  q={q}, head={A[q][idx[q]]}")
        raise RuntimeError(
            "Cannot merge: no ready operations left but queues non‐empty")

    return Combined

    print(Combined)
